import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

interface RepoState {
    repos: any[];
    loading: boolean;
    error: string | null;
    sortField: string;
    sortOrder: 'asc' | 'desc';
    selectedRepo: any | null;  // Добавляем selectedRepo
  }

  const initialState: RepoState = {
    repos: [],
    loading: false,
    error: null,
    sortField: 'stargazers_count',
    sortOrder: 'desc',
    selectedRepo: null,  // Устанавливаем начальное значение
  };



export const fetchRepos = createAsyncThunk(
  'repos/fetchRepos',
  async (query: string) => {
    const response = await axios.get(`https://api.github.com/search/repositories?q=${query}`);
    return response.data.items;
  }
);

const repoSlice = createSlice({
    name: 'repos',
    initialState,
    reducers: {
      setSortField(state, action) {
        state.sortField = action.payload;
      },
      setSortOrder(state, action) {
        state.sortOrder = action.payload;
      },
      selectRepo(state, action) {  // Добавляем действие selectRepo
        state.selectedRepo = action.payload;
      },
    },
    extraReducers: (builder) => {
      builder.addCase(fetchRepos.pending, (state) => {
        state.loading = true;
        state.error = null;
      });
      builder.addCase(fetchRepos.fulfilled, (state, action) => {
        state.loading = false;
        state.repos = action.payload;
      });
      builder.addCase(fetchRepos.rejected, (state, action) => {
        state.loading = false;
        state.error = action.error.message ?? 'Error fetching repositories';
      });
    },
  });

  export const { setSortField, setSortOrder, selectRepo } = repoSlice.actions;  // Экспортируем новое действие
  export default repoSlice.reducer;
