import React from "react";
import { useSelector } from "react-redux";
import { RootState } from "../redux/store";
import { Container, Grid, Typography } from "@mui/material";
import SearchBar from "./SearchBar";
import RepoTable from "./RepoTable";
import RepoDetails from "./RepoDetails";
import styles from '../styles/App.module.scss';

/**
 * Компонент приложения, который включает строку поиска, таблицу репозиториев и детали репозитория.
 */
const App: React.FC = () => {
  const selectedRepo = useSelector((state: RootState) => state.repos.selectedRepo);

  return (
    <Container className={styles.container} maxWidth={false}>
      <SearchBar />
      {!selectedRepo ? (
        <Typography variant="h4" className={styles.greetingMessage}>
          Привет
        </Typography>
      ) : (
        <Grid container spacing={2} className={styles.gridContainer}>
          <Grid item xs={12} className={styles.repoTableContainer}>
            <RepoTable />
          </Grid>
          <Grid item xs={12} sm={4} className={styles.repoDetailsContainer}>
            <RepoDetails />
          </Grid>
        </Grid>
      )}
    </Container>
  );
};

export default App;
